import React from 'react';
import { Form, FormInput, FormGroup, Button, Card, CardBody, CardTitle, Progress } from "shards-react";
import {
    Table,
    Pagination,
    Select,
    Row,
    Col,
    Divider,
    Slider,
    Rate 
} from 'antd'
import { format } from 'd3-format';

import MenuBar from '../components/MenuBar';
import { relJobVictim } from '../fetcher'
const wideFormat = format('.3r');
const { Option } = Select;
function handleChange(value) {
    console.log(`selected ${value}`);
}
class PcrimePage extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            jobResults: [],
            value:"disabled"

        }

        this.updateSearchResults = this.updateSearchResults.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }



    handleTimes_movedQueryChange(value) {
        this.setState({ Times_moved_lowQuery: value[0] })
        this.setState({ Times_moved_highQuery: value[1] })
    }

    updateSearchResults() {
        relJobVictim().then(res => {
            this.setState({ personsResults: res.results })
        })
        //TASK 23: call getPlayerSearch and update playerResults in state. See componentDidMount() for a hint
    }

    componentDidMount() {
        relJobVictim().then(res => {
            this.setState({ jobResults: res.results })
        })
    }
    handleChange(event) {
        this.setState({value: event.target.value})
      }

    handleSubmit(event) {
        alert('Your favorite flavor is: ' + this.state.value);
        event.preventDefault();
    }
    render() {
        return (

            <div>

                <MenuBar />

                <form onSubmit={this.handleSubmit}>
                <label>
                Pick your inquiry: 
                <Select value={this.state.value} onChange={this.handleChange}>
                    <Option value="race">Race</Option>
                    <Option value="sex">Sex</Option>
                    <Option value="job">Job</Option>

                </Select>
                </label>
                <input type="submit" value="Submit" />
            </form>


            </div>
        )
    }
}

export default PcrimePage
